package utils

import (
	"testing"

	"github.com/stretchr/testify/require"
)

func TestCompileSol(t *testing.T) {
	var expectedBytecode = ""
	bytecode, err := CompileSol("../contracts/CurrentYear.sol")

	require.NoError(t, err, "error should be nil")
	require.Equal(t, expectedBytecode, bytecode, "compiled bytecode for ../contracts/CurrentYear.sol should be equal")

	bytecode, err = CompileSol("") // Empty dir, should cause an error
	require.Error(t, err, "error should not be nil")
}
